/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `W2W_CODE` (
  `cid` varchar(12) NOT NULL DEFAULT '' COMMENT '기초코드',
  `depth` tinyint(1) DEFAULT '1',
  `code_name` varchar(255) NOT NULL DEFAULT '' COMMENT '코드분류명',
  `state` tinyint(1) DEFAULT '9' COMMENT '사용여부 1:사용, 9:사용안함',
  `order_num` int(10) NOT NULL DEFAULT '0' COMMENT '출력순서',
  `extra_01` varchar(255) NOT NULL DEFAULT '' COMMENT '여분1',
  `extra_02` varchar(255) NOT NULL DEFAULT '' COMMENT '여분2',
  `extra_03` varchar(255) NOT NULL DEFAULT '' COMMENT '여분3',
  `extra_04` varchar(255) NOT NULL DEFAULT '' COMMENT '여분4',
  `extra_05` varchar(255) NOT NULL DEFAULT '' COMMENT '여분5',
  `extra_06` varchar(255) NOT NULL DEFAULT '' COMMENT '여분6',
  `extra_07` varchar(255) NOT NULL DEFAULT '' COMMENT '여분7',
  `extra_08` varchar(255) NOT NULL DEFAULT '' COMMENT '여분8',
  `extra_09` varchar(255) NOT NULL DEFAULT '' COMMENT '여분9',
  `extra_10` varchar(255) NOT NULL DEFAULT '' COMMENT '여분10',
  PRIMARY KEY (`cid`),
  KEY `State_Index` (`state`),
  KEY `Depth_Index` (`depth`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

