DROP TABLE IF EXISTS `W2W_CONFIG`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `W2W_CONFIG` (
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '사이트 제목',
  `site_url` varchar(255) NOT NULL DEFAULT '' COMMENT 'url',
  `master_id` varchar(255) DEFAULT NULL COMMENT '관리자 ID',
  `main_logo` varchar(255) DEFAULT '' COMMENT '메인로고',
  `main_bottom_logo` varchar(255) DEFAULT '' COMMENT '메인하단로고',
  `logo` varchar(255) DEFAULT NULL COMMENT '로고',
  `s_logo` varchar(255) DEFAULT NULL COMMENT '게시판용 로고',
  `favicon` varchar(255) DEFAULT '' COMMENT '파비곤',
  `use_point` tinyint(1) NOT NULL DEFAULT '0' COMMENT '포인트 사용여부',
  `login_point` int(11) NOT NULL DEFAULT '0' COMMENT '로그인 포인트',
  `use_homepage` tinyint(1) NOT NULL DEFAULT '0' COMMENT '가입시 홈페이지 사용여부',
  `req_homepage` tinyint(1) NOT NULL DEFAULT '0' COMMENT '홈페이지 필수여부',
  `use_tel` tinyint(1) NOT NULL DEFAULT '0' COMMENT '가입시 전화번호 사용여부',
  `req_tel` tinyint(1) NOT NULL DEFAULT '0' COMMENT '전화번호 필수여부',
  `use_hp` tinyint(1) NOT NULL DEFAULT '0' COMMENT '가입시 휴대폰 사용여부',
  `req_hp` tinyint(1) NOT NULL DEFAULT '0' COMMENT '휴대폰 필수 여부',
  `use_addr` tinyint(1) NOT NULL DEFAULT '0' COMMENT '주소 사용여부',
  `req_addr` tinyint(1) NOT NULL DEFAULT '0' COMMENT '주소 필수 여부',
  `use_profile` tinyint(1) NOT NULL DEFAULT '0' COMMENT '가입시 프로필 사용 여부',
  `req_profile` tinyint(1) NOT NULL DEFAULT '0' COMMENT '프로필 필수 여부',
  `use_email` tinyint(1) NOT NULL DEFAULT '0' COMMENT '가입시 이메일 사용여부',
  `req_email` tinyint(1) NOT NULL DEFAULT '0' COMMENT '이메일 필수 여부',
  `use_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '추천인 사용여부',
  `recommend_point` int(11) NOT NULL DEFAULT '0' COMMENT '추천인 포인트',
  `board_filter` text NOT NULL COMMENT '게시판 필터링',
  `possible_ip` text NOT NULL COMMENT '접근IP',
  `intercept_ip` text NOT NULL COMMENT '차단 IP',
  `register_level` tinyint(1) NOT NULL DEFAULT '0' COMMENT '가입후 권한 최소:1',
  `register_point` int(11) NOT NULL DEFAULT '0' COMMENT '가입 포인트',
  `email_master` tinyint(1) NOT NULL DEFAULT '0' COMMENT '회원가입시 관리자 이메일 전송 여부',
  `email_member` tinyint(1) NOT NULL DEFAULT '0' COMMENT '회원가입시 회원에게 메일 전송 여부',
  `prohibit_id` text NOT NULL COMMENT '금지 아이디',
  `prohibit_email` text NOT NULL COMMENT '금지 이메일',
  `stipulation` text NOT NULL COMMENT '가입약관',
  `privacy` text NOT NULL COMMENT '개인정보 취급방침',
  `termofuse_policy` text COMMENT '이용약관',
  `protection_policy` text COMMENT '청소년보호정책',
  `email_policy` text COMMENT '이메일무단수집거부',
  `copyright` text COMMENT '사용자 copyright',
  `visit` varchar(255) DEFAULT NULL COMMENT '접속자',
  `mod_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '수정일',
  `site_name` varchar(255) DEFAULT '',
  `site_description` varchar(255) DEFAULT '',
  `site_keywords` varchar(255) DEFAULT '',
  `sms_site` tinyint(2) NOT NULL DEFAULT '1' COMMENT '1:아이코드',
  `sms_id` varchar(255) DEFAULT NULL COMMENT 'SMS 아이디',
  `sms_pw` varchar(255) DEFAULT NULL COMMENT 'SMS 비밀번호',
  `mail_id` varchar(255) DEFAULT '' COMMENT '메일아이디',
  `mail_pwd` varchar(255) DEFAULT '' COMMENT '메일비밀번호',
  `mail_host` varchar(255) DEFAULT '' COMMENT '메일호스트',
  `mail_port` varchar(255) DEFAULT '' COMMENT '메일포트',
  `naver_client_id` varchar(255) DEFAULT '' COMMENT '네이버 client id',
  `naver_client_pw` varchar(255) DEFAULT '' COMMENT '네이버 client pw',
  `kakao_key` varchar(255) DEFAULT '' COMMENT '카카오키',
  `google_client_id` varchar(255) DEFAULT '' COMMENT '구글 client id',
  `google_client_pw` varchar(255) DEFAULT '' COMMENT '구글 client pw'
) ENGINE=MyISAM DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

