DROP TABLE IF EXISTS `W2W_DESIGN`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `W2W_DESIGN` (
  `idx` int(11) NOT NULL AUTO_INCREMENT COMMENT '일련번호',
  `d_type` char(1) DEFAULT 'A' COMMENT '디자인타입',
  `d_type2` char(1) DEFAULT 'M' COMMENT 'M:메인, S:서브, D:상세, C:공용(디자인C만)',
  `d_delflag` enum('Y','N') DEFAULT 'Y' COMMENT '사용유무',
  `d_delflag_YN` enum('Y','N') DEFAULT 'N' COMMENT '사용유무만 확인',
  `d_sub_cate` varchar(30) DEFAULT '' COMMENT '[서브메뉴] 카테고리',
  `d_pos` varchar(2) DEFAULT '' COMMENT '위치',
  `d_pos_title` varchar(255) DEFAULT '' COMMENT '[위치] 제목',
  `d_pos_cate` varchar(30) DEFAULT '' COMMENT '[위치] 카테고리',
  `d_pos_limit` int(11) DEFAULT '0' COMMENT '[위치] 게시물수',
  `d_pos_delflag` enum('Y','N') DEFAULT 'Y' COMMENT '[위치] 노출유무',
  `d_pos_ordernum` varchar(100) DEFAULT '' COMMENT '[위치] 정렬순서 (문자열)',
  `d_pos_viewnum` int(11) DEFAULT '0' COMMENT '[위치] 정렬순서(홈페이시에서)',
  `d_regid` varchar(255) DEFAULT '' COMMENT '등록id',
  `d_regdate` datetime DEFAULT NULL COMMENT '등록일',
  PRIMARY KEY (`idx`),
  KEY `d_type` (`d_type`),
  KEY `d_type_2` (`d_type`,`d_type2`),
  KEY `d_type_3` (`d_type`,`d_type2`,`d_delflag`),
  KEY `d_type_4` (`d_type`,`d_type2`,`d_delflag`,`d_sub_cate`),
  KEY `d_type_5` (`d_type`,`d_type2`,`d_delflag`,`d_sub_cate`,`d_pos`,`d_delflag_YN`),
  KEY `d_type_6` (`d_type`,`d_type2`,`d_delflag`,`d_pos`,`d_delflag_YN`),
  KEY `d_type_7` (`d_type`,`d_type2`,`d_sub_cate`),
  KEY `d_type_8` (`d_type`,`d_type2`,`d_sub_cate`,`d_pos`),
  KEY `d_type_9` (`d_type`,`d_type2`,`d_sub_cate`,`d_pos`,`d_pos_cate`),
  KEY `d_type_10` (`d_type`,`d_type2`,`d_sub_cate`,`d_pos`,`d_pos_cate`,`d_pos_delflag`),
  KEY `d_type_11` (`d_type`,`d_type2`,`d_sub_cate`,`d_pos`,`d_pos_cate`,`d_pos_delflag`,`d_pos_viewnum`)
) ENGINE=MyISAM AUTO_INCREMENT=641 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

