DROP TABLE IF EXISTS `W2W_NEWS_CATEGORY`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `W2W_NEWS_CATEGORY` (
  `idx` int(11) NOT NULL AUTO_INCREMENT COMMENT '일련번호',
  `nc_code` varchar(10) DEFAULT '' COMMENT '코드',
  `nc_name` varchar(255) DEFAULT '' COMMENT '명칭',
  `nc_area` text COMMENT '지역',
  `nc_category` text COMMENT '분류',
  `nc_eng_area` text COMMENT '영문지역(코드)',
  `nc_eng_area_name` text COMMENT '영문지역(명칭)',
  `nc_eng_category` text COMMENT '영문분류',
  `nc_writer_id` text COMMENT '작성자 id',
  `nc_zoom` char(2) DEFAULT '' COMMENT '줌채널',
  `nc_ranky` varchar(255) DEFAULT '' COMMENT '랭키',
  `nc_ordernum` int(11) DEFAULT '9999' COMMENT '정렬',
  `nc_delflag` char(1) DEFAULT '1' COMMENT '사용유무 1:사용',
  `nc_board` char(1) DEFAULT '0' COMMENT '게시판사용유무 1:사용',
  `nc_board_code` varchar(100) DEFAULT '' COMMENT '게시판코드',
  `nc_report` char(1) DEFAULT '' COMMENT '제보게시판 1:사용',
  PRIMARY KEY (`idx`),
  KEY `nc_code` (`nc_code`),
  KEY `nc_code_2` (`nc_code`,`nc_ordernum`),
  KEY `nc_code_3` (`nc_code`,`nc_ordernum`,`nc_delflag`)
) ENGINE=MyISAM AUTO_INCREMENT=96 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

